import { IUniversityClass, IUniversityGrades } from "../types/api_types";
import { studentGrade } from "../types/api_types";
import { useState, useEffect } from 'react';
import { MY_BU_ID, BASE_API_URL, GET_DEFAULT_HEADERS } from "../globals";

/**
