 */
//const [currStudentGrades, setStudentGrades] = useState<number[]>([]);
let currStudentGrades: number[];

async function callGrades(classId: string, studentId: string): Promise<void> {
  const fetchStudentGrades = async () => {
    const res = await fetch(`${BASE_API_URL}/student/listGrades/${studentId}/${classId}?buid=${MY_BU_ID}`, {
      method: "GET",
      headers: GET_DEFAULT_HEADERS()
    });

    const json:IUniversityGrades = await res.json();

    const gradesObject = json.grades
    const gradeValues = Object.values(gradesObject).flatMap((nestedObject) => Object.values(nestedObject).map((str) => parseInt(str, 10)));

    currStudentGrades = gradeValues;
    //console.log("Current student's grades:");
    //console.log(currStudentGrades);
  }
  fetchStudentGrades();
};

function gradeCalculation(classId, classAssessments, classStudents){
  const [currStudentGrades, setStudentGrades] = useState<number[]>([]);

  async function callGrades(classId: string, studentId: string): Promise<void> {
      const res = await fetch(`${BASE_API_URL}/student/listGrades/${studentId}/${classId}?buid=${MY_BU_ID}`, {
        method: "GET",
        headers: GET_DEFAULT_HEADERS()
      });

      const json:IUniversityGrades = await res.json();

      const gradesObject = json.grades
      const gradeValues = Object.values(gradesObject).flatMap((nestedObject) => Object.values(nestedObject).map((str) => parseInt(str, 10)));

      setStudentGrades(gradeValues);
      console.log("Current student's grades:");
      console.log(currStudentGrades);
  };

  function calcGrade(grades: number[], weight: number[]) {
    const summing = grades.reduce((accumulator, currentValue, index) => {
      const product = currentValue * weight[index];
      return accumulator + product;
    }, 0);
    return summing
  }

  async function calculateStudentFinalGrade(
    classId: string, //"A123"
    studentId: string, // "U123"
    weight: number[], // [90]
    //klass: IUniversityClass what is this variable supposed to rep?
  ): Promise<number> {
    await callGrades(classId, studentId);
    console.log("IM HEERE")
    console.log(currStudentGrades)
    const grade = calcGrade(currStudentGrades, weight)
    return grade; //with the classAssignments we know the weight of each assignment
    // with /student/listGrades/studentID/classID we get the grade of these assignemnts
  }

  /**
   * You need to write this function! You might want to write more functions to make the code easier to read as well.
   * 
   *  If you are reading here and you haven't read the top of the file...go back.
   * 
   * @param classID The ID of the class for which we want to calculate the final grades
   * @returns Some data structure that has a list of each student and their final grade.
   */
  async function calcAllFinalGrade(classId: string, assessments: number[], students: string[]): Promise<studentGrade[]> {
    const grades: studentGrade[] = []
    console.log("this is weight:")
    console.log(assessments);
    await Promise.all(students.map(async (studentId) => {
      const grade = await calculateStudentFinalGrade(classId, studentId, assessments);
      const studentGrade = { studentId, grade };
      grades.push(studentGrade);
    }));
  /*   students.forEach(async (studentId) => {
      var grade = await calculateStudentFinalGrade(classId, studentId, assessments)
      //console.log(grade)
      var studentGrade = {studentId, grade}
      grades.push(studentGrade);
    }) */
    console.log("All Grades for this Class:")
    console.log(grades)
    return grades; //grab all students in this class and fun above func on each
  }

  useEffect(() => {calcAllFinalGrade(classId, classAssessments, classStudents)}, []);
}

export default gradeCalculation
