import React, { useEffect, useState, useRef } from "react";
import Grid from "@mui/material/Unstable_Grid2";
import { Select, Typography, MenuItem } from "@mui/material";
//import { calcAllFinalGrade } from "./utils/calculate_grade";
/**
 * You will find globals from this file useful!
 */
import { MY_BU_ID, BASE_API_URL, SEMESTER, TOKEN, GET_DEFAULT_HEADERS } from "./globals";
import { IUniversityClass, IUniversityAssignments, IUniversityGrades, IUniversityStudents } from "./types/api_types";
import GradeTable from "./components/GradeTable";
import GradeTableProps from "./types/api_types";

function App() {
  // You will need to use more of these!
  const [currClassId, setCurrClassId] = useState<string>("");
  const [classList, setClassList] = useState<IUniversityClass[]>([]);
  const [currClassName, setClassName] = useState<string>("");
  const [currData, setData] = useState<GradeTableProps["studentData"]>([]);
  const [allGrades, setGrades] = useState<number[]>([]);
  const [currFilteredClasses, setFilteredClasses] = useState<string[]>([]);
  const [currFilteredStudents, setFilteredStudents] = useState<string[]>([]);
  const [currStudentNames, setStudentNames] = useState<string[]>([]);
  const [currFilteredAssignments, setFilteredAssignments] = useState<number[]>([]);
  const [currStudent, setStudent] = useState<string>("");
  const [isLoading, setIsLoading] = useState(true);
  const studentData: Array<{
    studentId: string;
    studnetName: string;
    classId: string;
    className: string;
    semester: string;
    finalGrade: number;
  }> = [];

  /**
   * This is JUST an example of how you might fetch some data(with a different API).
   * As you might notice, this does not show up in your console right now.
   * This is because the function isn't called by anything!
   *
   * You will need to lookup how to fetch data from an API using React.js
   * Something you might want to look at is the useEffect hook.
   *
   * The useEffect hook will be useful for populating the data in the dropdown box.
   * You will want to make sure that the effect is only called once at component mount.
   *
   * You will also need to explore the use of async/await.
   *
   */

  useEffect(() => { // this happens first thing when App opens
    const fetchSomeData = async () => {
        const res = await fetch(`${BASE_API_URL}/class/listBySemester/${SEMESTER}?buid=${MY_BU_ID}`, {
          method: "GET",
          headers: GET_DEFAULT_HEADERS()
        });
        const json = await res.json();
        console.log(json);
        //console.log(res.status);
        setClassList(json); //I don't think I need this, make this instead, classList
        const filteredValues = json.map((item:IUniversityClass) => item.classId);
        console.log("The classes fetched", filteredValues);
        setFilteredClasses(filteredValues);
    };
    const fetchClassName = async() => {
      const res = await fetch(`${BASE_API_URL}/class/GetById/${currClassId}?buid=${MY_BU_ID}`, {
        method: "GET",
        headers: GET_DEFAULT_HEADERS()
      });
      const json = await res.json();
      //const filterName = json.title;
      //console.log("this is class name:", filterName)
      setClassName(json.title);
    }
    fetchSomeData();
    fetchClassName().then(() => {setIsLoading(false);});
  }, []);

  useEffect(() => {
    if (currClassId !== "") {
      console.log(currClassId);
      const fetchStudents = async () => {
        const res = await fetch(`${BASE_API_URL}/class/listStudents/${currClassId}?buid=${MY_BU_ID}`, {
          method: "GET",
          headers: GET_DEFAULT_HEADERS()
        });
        const json = await res.json();
        console.log("student fetch response", json);
        //console.log (json);
        setFilteredStudents(json); 
      };

      const fetchAssignments = async () => {
        const res = await fetch(`${BASE_API_URL}/class/listAssignments/${currClassId}?buid=${MY_BU_ID}`, {
          method: "GET",
          headers: GET_DEFAULT_HEADERS()
        });
        const json = await res.json();
        //console.log("assignments fetch response:");
        //console.log(json);

        const filteredValues: number[] = json.map((item:IUniversityAssignments) => item.weight );

        setFilteredAssignments(filteredValues);
      };

      fetchStudents();
      fetchAssignments();

        //calcAllGrades();
        //calcAllFinalGrade(currClassId, currFilteredAssignments, currFilteredStudents).then((result)=>{setGrades(result)}); //gonna need to put this into a useState Kinda thing
    }
  }, [currClassId]);

  useEffect(() => {
    if (currFilteredStudents.length > 0 && currFilteredAssignments.length > 0) {
      console.log("students:", currFilteredStudents);
      const filteredValues: string[] = [];
      //console.log("filtered assignments:");
      //console.log(currFilteredAssignments);

      const fetchAndSetGrades = async () => {
        const grades = await calcAllGrades();
        setGrades(grades);
      };

      currFilteredStudents.forEach((student) => {
        fetchStudentNames(student).then((result)=>{filteredValues.push(result)});
        //console.log("what was filtered:", filteredValues)
      });
      setStudentNames(filteredValues);
      fetchAndSetGrades();
    }
  }, [currFilteredStudents, currFilteredAssignments]);

  useEffect(() => {
    if (allGrades.length > 0) {
      hookUpData();
      console.log("For Grid Table:", studentData);
      setData(studentData);
      setIsLoading(false);
    }
  }, [allGrades])

  const fetchStudentNames = async (student: string) => {
    const res = await fetch(`${BASE_API_URL}/student/GetById/${student}?buid=${MY_BU_ID}`, {
      method: "GET",
      headers: GET_DEFAULT_HEADERS()
    });
    const json = await res.json();
    //console.log("names fetch response:", json);
    //console.log(json);

    const filteredData: string = json[0].name;
    return filteredData;
    //filteredValues.push(filteredData)
  }; 

  const fetchStudentGrades = async (student: string) => {
    const res = await fetch(`${BASE_API_URL}/student/listGrades/${student}/${currClassId}?buid=${MY_BU_ID}`, {
      method: "GET",
      headers: GET_DEFAULT_HEADERS()
    });

    const json = await res.json();
    const gradesObject = json.grades[0]; // Assuming there's only one object in the grades array
    const gradeValues: number[] = Object.values(gradesObject).map(Number);
    //console.log("grades in array form:");
    //console.log(gradeValues);

    //console.log("Current student's grades:");
    //console.log(gradeValues);

    return gradeValues
  };

  async function calcAllGrades() {
    const grades: number[] = [];
    const gradePromises = currFilteredStudents.map((student) => fetchStudentGrades(student));

    const gradeResults = await Promise.all(gradePromises);
    gradeResults.forEach((gradeValues) => {
      const grade = calcGrade(gradeValues, currFilteredAssignments);
      grades.push(grade);
    });

    console.log("All grades calculated:", grades);
    return grades;
  }

  function calcGrade(grades: number[], weight: number[]) {
    const summing = grades.reduce((accumulator, currentValue, index) => {
      const product = currentValue * ~~(weight[index]/100);
      return accumulator + product;
    }, 0);
    return summing;
  };

  function hookUpData(){
    allGrades.forEach((grade, index) => {
      const studentId = currFilteredStudents[index];
      const studentName = currStudentNames[index];
      //const classId = currClassId;
      //const className = currClassName;
      //const semester = SEMESTER;
      //const finalGrade = grade;
      const data = createData(studentId, studentName, currClassId, currClassName, SEMESTER, grade);
      studentData.push(data);
    });
  }

  function createData(
    studentId: string,
    studnetName: string,
    classId: string,
    className: string,
    semester: string,
    finalGrade: number,
  ) {
    return { studentId, studnetName, classId, className, semester, finalGrade };
  }

  return (
    <div style={{ width: "100vw", height: "100vh" }}>
      <Grid container spacing={2} style={{ padding: "1rem" }}>
        <Grid xs={12} container alignItems="center" justifyContent="center">
          <Typography variant="h2" gutterBottom>
            Spark Assessment
          </Typography>
        </Grid>
        <Grid xs={12} md={4}>
          <Typography variant="h4" gutterBottom>
            Select a class
          </Typography>
          <div style={{ width: "100%" }}>
            <Select fullWidth={true} label="Class"
            value = {currClassId}
            onChange={x => setCurrClassId(x.target.value)}>
              {/* You'll need to place some code here to generate the list of items in the selection */}
              {currFilteredClasses.map((classId, index) => (
              <MenuItem key={index} value={classId}>
                {classId}
              </MenuItem>
            ))}
            </Select>
          </div>
        </Grid>
        <Grid xs={12} md={8}>
          <Typography variant="h4" gutterBottom>
            Final Grades
          </Typography>
          {isLoading ? (<div>Loading data...</div>) : ("")}
          <GradeTable studentData={currData} />
        </Grid>
      </Grid>
    </div>
  );
}

export default App;
